    public void ExampleTest5()
    {
        var input =
            "";
        Assert.IsFalse(new Solution().ValidPalindrome(input));
    }

    [Test]
    public void ExampleTest6()
    {
        var input = "deeee";
        Assert.IsTrue(new Solution().ValidPalindrome(input));
    }

    [Test]
    public void ExampleTest7()
    {
        var input = "cbbcc";
        Assert.IsTrue(new Solution().ValidPalindrome(input));
    }
